// src/pages/Home.js

import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Home = () => {
  const [movies, setMovies] = useState([]);

  useEffect(() => {
    axios.get('/api/movies/home', { withCredentials: false })
      .then(response => {
        setMovies(response.data);
      })
      .catch(error => console.error('Error fetching movies:', error));
  }, []);

  return (
    <div>
      <h1>Now Playing</h1>
      <div className="movie-list" style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'space-around' }}>
        {movies.map(movie => (
          <div key={movie.id} className="movie-card" style={{ margin: '20px', textAlign: 'center' }}>
            <img 
              src={movie.posterUrl} 
              alt={movie.title} 
              style={{ width: '200px', height: '300px', objectFit: 'cover' }}
              onError={(e) => e.target.style.display = 'none'}
            />
            <h2>{movie.title}</h2>
            <p>{movie.synopsis}</p>
            <a href={`/movies/${movie.id}`}>View Details</a>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Home;
